FROM last_touch ltJOIN page_visits pv    ON lt.user_id = pv.user_id    AND lt.last_touch_at = pv.timestamp      )      SELECT lt_attr.utm_source, lt_attr.utm_campaign, COUNT(last_touch_at) FROM lt_attr      GROUP BY 1, 2 ORDER BY 3 DESC;